    public P() {
        super("Day 1: Not Quite Lisp");
    }

    @Override
    public String solvePart1() {
        int floor = 0;
        for (char c : input.toCharArray()) {
            switch (c) {
                case '(':
                    floor++;
                    break;
                case ')':
                    floor--;
                    break;
            }
        }
        return floor + "";
    }

    @Override
    public String solvePart2() {
        int floor = 0;
        for (int i = 0; i < input.toCharArray().length; i++) {
            char c = input.toCharArray()[i];
            switch (c) {
                case '(':
                    floor++;
                    break;
                case ')':
                    floor--;
                    break;
            }

            if (floor == -1) {
                // Indexing starts at 1
                return (i + 1) + "";
            }
        }
        return "Did not get there";
    }
